const sreController = {};
const pool = require('../database');

sreController.jsonMaterias = async (req, res)=>{
    var consultaCarreras = "SELECT DISTINCT * FROM carrera";
    var consutaMaterias = "SELECT DISTINCT materia.* FROM materia, imparte WHERE imparte.idMateria = materia.idMateria AND imparte.idCarrera = ? AND materia.ano = ? ORDER BY materia.cuatrimestre ASC";
    var json = {};
    var carreras = await pool.query(consultaCarreras);
    json.carreras = carreras;
    //ahora creamos la estructura de las materias
    var materias = [];

    for (let i = 0; i < carreras.length; i++) {
        const carrera = carreras[i];
        var materiasCarrera = [];
        for(var j = 1; j <= carrera.duracion; j++){
            var materiasAnio = [];
            var materiasAnio = await pool.query(consutaMaterias,[carrera.idCarrera, j]);
            materiasCarrera.push(materiasAnio);
        }
        materias.push(materiasCarrera);
    }

    json.materias = materias;
    res.json(json);
}

module.exports = sreController;

const usuarioControler = {};
const pool = require('../database');
const jwt = require('jsonwebtoken');
const key = require('../config.json');
const md5 = require('md5');

usuarioControler.validar = (req, res) => {
    var usuario = req.params.usuario;
    var contrasenia = md5(req.params.contrasenia);
    var consulta = "SELECT * FROM usuario WHERE usuario.usuario = ? AND usuario.pass = ?";

    pool.query(consulta, [usuario, contrasenia], (err, usuario) => {
        if(err){
            res.json(err);
        }
        console.log(usuario.length);
        if (usuario.length > 0 && usuario != null) {
            var token = jwt.sign({"token":usuario[0].token}, key.clave);
            console.log(token);
            res.json({"token":token});
        }else{
            res.json({"mensaje": "el usuario no es valido o la contrasea es incorrecta"});
        }
    });
};

usuarioControler.validarLogin = (req, res) => {
    var usuario = req.body.usuario;
    var contrasenia = req.body.contrasenia;
    var consulta = "SELECT * FROM `usuario` WHERE usuario = ? AND pass = ?";

    pool.query(consulta, [usuario, contrasenia], (err, usuario)=>{
        if(err){
            console.log(err);
        }
        if(usuario.length > 0){
            req.session.user = {
                "nombre" : usuario[0].usuario,
                "tipo" : usuario[0].tipo
            };
            console.log(req.session.user);
            res.redirect("/administrar/");
        }else{
            res.redirect("/login");
        }
    });
}

module.exports = usuarioControler;

